-- author: Your Name
-- desc:   Basic Match-3 tile game
-- version: 0.1
-- script:  lua

M3_Game = require("m3_lib")

-- Handle input
function handle_input()
 if btnp(0) then -- Up
    M3_Game:move_up()
 elseif btnp(1) then -- Down
M3_Game:move_down()
 elseif btnp(2) then -- Left
    M3_Game:move_left()

 elseif btnp(3) then -- Right
    M3_Game:move_right()    
 elseif btnp(4) then -- Select -- d
    if input_mode == InputModes.Select then
       input_mode = InputModes.Swap
    else
       input_mode = InputModes.Select
    end
 end
end


-- TIC function
function TIC()
 handle_input()
 M3_Game:draw()
end

function BOOT()
   -- Initialize the game
   M3_Game:init()
end

