-- Missile Command for TIC-80

Queue = require("q")
MC_Game = require("mc_lib")

function TIC()
    if btnp(4) then -- 'Z' key to shoot
        MC_Game:add_bullet(SCREEN_WIDTH // 2, SCREEN_HEIGHT - 10)
    end

    MC_Game:update()
    MC_Game:draw()
end

function BOOT()
   MC_Game:init()
end

